from flask_mongoengine import MongoEnginefrom mongoengine import *from app import appdb = MongoEngine(app)class User(db.Document):    enter_name = StringField()    name_from_telegram = StringField()    telegram_id = IntField(required=True)    next_step_func_name = StringField()    def __str__(self):        return 'enter_name: {}, name_from_telegram: {}'.format(self.enter_name, self.name_from_telegram)