from flask_mongoengine import MongoEnginefrom mongoengine import StringFieldfrom app import appdb = MongoEngine(app)class User(db.Document):    enter_name = StringField()    name_from_telegram = StringField(required=True)    telegram_id = StringField(required=True)    def __str__(self):        return 'enter_name: {}, name_from_telegram: {}'.format(self.enter_name, self.name_from_telegram)